import React, { Component } from "react";
import axios from "axios";


class MedicineOrders extends Component {

    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            medicineOrdersList: []
        };
    }
    componentDidMount() {
        this.refreshList();
    }
    refreshList = () => {
        axios
            .get("medicineOrders/")
            .then(res => this.setState({ medicineOrdersList: res.data }))
            .catch(err => console.log(err));
    };

    toggle = () => {
        this.setState({ modal: !this.state.modal });
    };



    renderCustomers = () => {
        const medicineOrders = this.state.medicineOrdersList;
        return medicineOrders.map(item => (
            <li key={item.id}>
            <span>
              {item.name} {item.surname} {item.age}
            </span>
                <span>
              <button onClick={() => this.editItem(item)} className="btn btn-info ml-2"> Edit </button>
              <button onClick={() => this.handleDelete(item)} className="btn btn-danger ml-2"> Delete </button>
            </span>
            </li>
        ));
    };

    render() {
        return (
            <main className="content">
                <ul>
                    {this.renderCustomers()}
                </ul>
            </main>
        );
    }

}

export default MedicineOrders;
