import React, { Component } from "react";
import axios from "axios";
import {Button, Form, FormGroup, Input, Label} from "reactstrap";



class AddMedicineOrder extends Component {

    constructor(props) {
        super(props);
        this.state = {
            customer: {
                id: null,
                name: "",
                surname: "",
                pesel: null,
                age: null
            },
            orderItems: [{ medicine: "", amount: null}],
            customerSearchValue: {},
            customersList: []
        };
        this.onCustomerSearchChange = this.onCustomerSearchChange.bind(this);
    };

    onCustomerSearchChange = e => {
        let { name, value } = e.target;
        const customerSearchValue = { [name]: value };
        this.setState({ customerSearchValue });
    };

    onCustomerSearchSubmit = () => {
        let customerSearchValue = this.state.customerSearchValue;
        if (customerSearchValue) {
            axios
                .get(`customers/?pesel=${customerSearchValue['pesel']}`, customerSearchValue)
                .then(res => this.setState({ customersList: res.data }))
                .catch(err => console.log(err));
        }
    };

    onCustomerSelect = item => {
        this.setState({customer: item});
    };



    renderCustomers = () => {
        const customersList = this.state.customersList;
        return customersList.map(item => (
            <li key={item.id}>
                <button onClick={(e) => this.onCustomerSelect(item)}>
                    {item.name} {item.surname} {item.pesel}
                </button>
            </li>
        ));
    };

    addOrderItem = (e) => {
        this.setState((prevState) => ({
            orderItems: [...prevState.orderItems, {medicine: "", amount: null}],
        }));
    }

    render () {
        return (
            <div>
                <h3>
                    Dodaj nowe zam√≥wienie
                </h3>
                <h6>
                    Podsumowanie:
                </h6>
                <p>
                    Customer: {this.state.customer.pesel} {this.state.customer.name} {this.state.customer.surname}
                </p>
                <span>
                    <Form>
                        <FormGroup>
                            <Label for="pesel">Search customer</Label>
                            <Input
                                type="text"
                                name="pesel"
                                value={this.state.customerSearchValue.pesel}
                                onChange={this.onCustomerSearchChange}
                                placeholder="Enter customer pesel"
                            />
                            <Button onClick={this.addOrderItem}>
                                Add another medicine
                            </Button>
                            {
                                this.state.orderItems( (val, idx) => {
                                    let medicineId = `medicine${idx}`, amountId = `amount${idx}`;
                                    return (
                                        <div key={idx}>
                                            <Label for={medicineId}>{`Medicine #${idx + 1}`}</Label>
                                            <Input
                                                type="text"
                                                name={medicineId}
                                                data-id={idx}
                                                id={medicineId}
                                                value={this.state.orderItems[idx].medicine}
                                            />
                                            <Label for={amountId}>Amount</Label>
                                            <Input
                                                type="text"
                                                name={amountId}
                                                data-id={idx}
                                                id={amountId}
                                                value={this.state.orderItems[idx].amount}
                                            />
                                        </div>
                                    )
                                })
                            }
                        </FormGroup>
                    </Form>
                    <Button color="primary" onClick={this.onCustomerSearchSubmit}>
                        Search
                    </Button>
                    <div>
                        <h6>Results:</h6>
                        {this.renderCustomers()}
                    </div>
                </span>
                <span>

                </span>
            </div>
        );
    }

}

export default AddMedicineOrder;
