import React, { Component } from "react";
import axios from "axios";
import {Button, Form, FormGroup, Input, Label, ModalFooter} from "reactstrap";


class SearchMedicines extends Component {

    constructor(props) {
        super(props);
        this.state = {
            medicinesList: [],
            searchValue: ""
        };
    }

    handleChange = e => {
        let { name, value } = e.target;
        const searchValue = { [name]: value };
        this.setState({ searchValue });
    };


    render() {
        return (
            <main className="content">
                <Form>
                    <FormGroup>
                        <Label for="name">Search medicine</Label>
                        <Input
                            type="text"
                            name="name"
                            onChange={this.handleChange}
                            placeholder="Search by medicine name"
                        />
                    </FormGroup>
                </Form>
                <Button color="primary" onClick={() => onSubmit(this.state.searchValue)}>
                    Search
                </Button>
            </main>
        );
    }


}

export default SearchMedicines;
