import React, { Component } from "react";
import axios from "axios";


class MedicineOrders extends Component {

    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            medicineOrdersList: [{
                customer: {
                    name: "",
                    surname: "",
                    age: null
                },
                total_price: null
            }]
        };
    }
    componentDidMount() {
        this.refreshList();
    }
    refreshList = () => {
        axios
            .get("medicineOrders/")
            .then(res => {
                    console.log(res.data);
                    console.log(res.data[0]['customer']);
                    console.log(res.data[0]['id']);
                    console.log(res.data[0]['total_price']);
                    this.setState({ medicineOrdersList: res.data });
                    console.log("state:");
                    console.log(this.state.medicineOrdersList);
                }
            ).catch(err => console.log(err));
    };

    toggle = () => {
        this.setState({ modal: !this.state.modal });
    };



    renderCustomers = () => {
        const medicineOrders = this.state.medicineOrdersList;
        return medicineOrders.map(item => (
            <span>
              {item}
            </span>
        ));
    };

    render() {
        return (
            <main className="content">
                <ul>
                    {this.renderCustomers()}
                </ul>
            </main>
        );
    }

}

export default MedicineOrders;
