import React, { Component } from "react";
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";

export default class UpdateOrderStatusModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            activeStatus: this.props.activeStatus,
            choices: this.props.choices
        };
        console.log(this.state.choices)
    }
    handleChange = e => {
        let { id, value } = e.target;
        const activeStatus = { value };
        this.setState({ activeStatus });
    };
    render() {
        const { toggle, onSave } = this.props;
        return (
            <Modal isOpen={true} toggle={toggle}>
                <ModalHeader toggle={toggle}> Update status </ModalHeader>
                <ModalBody>
                    <Form>
                        <FormGroup>
                            <Form.Control id="orderStatus" as="select" onChange={this.handleChange}>

                                <option>1</option>

                            </Form.Control>
                        </FormGroup>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" onClick={() => onSave(this.state.activeStatus)}>
                        Save
                    </Button>
                </ModalFooter>
            </Modal>
        );
    }
}
