import React, { Component } from "react";
import axios from "axios";
import {Button, Form, FormGroup, Input, Label} from "reactstrap";



class AddMedicineOrder extends Component {

    constructor(props) {
        super(props);
        this.state = {
            customer: {
                id: null,
                name: "",
                surname: "",
                age: null
            },
            orderItems: [],
            customerSearchValue: "",
            customersList: []
        };
    }

    onCustomerSearchChange = e => {
        let { name, value } = e.target;
        const customerSearchValue = { [name]: value };
        this.setState({ customerSearchValue });
    };

    onCustomerSearchSubmit = () => {
        let customerSearchValue = this.state.customerSearchValue;
        if (customerSearchValue) {
            axios
                .get(`customers/?name=${customerSearchValue['name']}`, customerSearchValue)
                .then(res => this.setState({ customersList: res.data }))
                .catch(err => console.log(err));
        }
    };

    onCustomerSelect = item => {
        this.setState({customer: item});
    };



    renderCustomers = () => {
        const customersList = this.state.customersList;
        return customersList.map(item => (
            <li key={item.id}>
                <button onClick={(e) => this.onCustomerSelect(item)}>
                    {item.name} {item.surname}
                </button>
            </li>
        ));
    };

    render () {
        return (
            <div>
                <h3>
                    Dodaj nowe zam√≥wienie
                </h3>
                <h6>
                    Podsumowanie:
                </h6>
                <p>
                    Customer: {this.state.customer.name} {this.state.customer.surname} {this.state.customer.id} {this.state.customer.age}}
                </p>
                <span>
                    <Form>
                        <FormGroup>
                            <Label for="name">Search customer</Label>
                            <Input
                                type="text"
                                name="name"
                                onChange={this.onCustomerSearchChange}
                                placeholder="Enter customer name"
                            />
                        </FormGroup>
                    </Form>
                    <Button color="primary" onClick={this.onCustomerSearchSubmit}>
                        Search
                    </Button>
                    <div>
                        <h6>Results:</h6>
                        {this.renderCustomers()}
                    </div>
                </span>
            </div>
        );
    }

}

export default AddMedicineOrder;
